		reg.exe add "HKU\DefaultUser\Software\Policies\Microsoft\Windows\Explorer" /v DisableSearchBoxSuggestions /t REG_DWORD /d 1 /f;
	};
	{
		reg.exe add "HKU\DefaultUser\Software\Microsoft\Windows\DWM" /v ColorPrevalence /t REG_DWORD /d 0 /f;
	};
	{
		reg.exe add "HKU\DefaultUser\Software\Microsoft\Windows\CurrentVersion\RunOnce" /v "UnattendedSetup" /t REG_SZ /d "powershell.exe -WindowStyle Normal -NoProfile -Command \""Get-Content -LiteralPath 'C:\Windows\Setup\Scripts\UserOnce.ps1' -Raw | Invoke-Expression;\""" /f;
	};
);

& {
  [float] $complete = 0;
  [float] $increment = 100 / $scripts.Count;
  foreach( $script in $scripts ) {
    Write-Progress -Activity 'Running scripts to modify the default user’’s registry hive. Do not close this window.' -PercentComplete $complete;
    '*** Will now execute command «{0}».' -f $(
      $str = $script.ToString().Trim() -replace '\s+', ' ';
      $max = 100;
      if( $str.Length -le $max ) {
        $str;
      } else {
        $str.Substring( 0, $max - 1 ) + '…';
      }
    );
    $start = [datetime]::Now;
    & $script;
    '*** Finished executing command after {0:0} ms.' -f [datetime]::Now.Subtract( $start ).TotalMilliseconds;
    "`r`n" * 3;
    $complete += $increment;
  }
} *>&1 >> "C:\Windows\Setup\Scripts\DefaultUser.log";

# List of Chocolatey packages to install
$packages = @(
    'firefox',
    'adobereader',
    '7zip',
    'vscode',
    'audacity',
    'discord',
    'hwmonitor',
    'openvpn-connect',
    'steam',
    'epicgameslauncher',
    'ea-app',
    'picard',
    'powershell-core',
    'transmission',
    'ventoy',
    'vlc',
    'winscp',
    'wireguard'
);

function Install-ChocoPackages {
	param(
		[string[]]$PackageList
	)
	
	Write-Host "Starting Chocolatey package installation..." -ForegroundColor Green;
	
	foreach ($package in $PackageList) {
		try {
			Write-Host "Installing package: $package" -ForegroundColor Yellow;
			$result = choco install $package -y --no-progress --limit-output;
			
			if ($LASTEXITCODE -eq 0) {
				Write-Host "Successfully installed: $package" -ForegroundColor Green;
			} else {
				Write-Host "Failed to install: $package (Exit code: $LASTEXITCODE)" -ForegroundColor Red;
			}
		} catch {
			Write-Host "Error installing $package : $($_.Exception.Message)" -ForegroundColor Red;
		}
	}
	
	Write-Host "Chocolatey package installation completed." -ForegroundColor Green;
}

# Install packages
Install-ChocoPackages -PackageList $packages;

# WSL Update Script
Write-Host "Updating WSL to latest version..." -ForegroundColor Green;

try {
    # Update WSL to the latest version
    Write-Host "Running wsl --update..." -ForegroundColor Yellow;
    wsl --update;
    
    # Set WSL2 as default version
    Write-Host "Setting WSL2 as default version..." -ForegroundColor Yellow;
    wsl --set-default-version 2;
    
    Write-Host "WSL update completed successfully!" -ForegroundColor Green;
    
} catch {
    Write-Host "Failed to update WSL: $($_.Exception.Message)" -ForegroundColor Red;
}

# NAS Share Mounting Script
# Configure your NAS shares below

# Define your NAS shares
$nasShares = @(
    @{
        UncPath = "\\polis\personal"
        DriveLetter = "Y:"
        Username = "fbobin"
        Password = ""
        Description = "personal"
    },
    @{
        UncPath = "\\polis\media"
        DriveLetter = "Z:"
        Username = "fbobin"
        Password = ""
        Description = "media"
    }
);

function Mount-NASShare {
    param(
        [string]$UncPath,
        [string]$DriveLetter,
        [string]$Username,
        [string]$Password,
        [string]$Description
    )
    
    try {
        Write-Host "Mounting $Description ($UncPath) to $DriveLetter..." -ForegroundColor Yellow;
        
        # Remove existing mapping if it exists
        if (Get-PSDrive -Name ($DriveLetter.TrimEnd(':')) -ErrorAction SilentlyContinue) {
            Write-Host "Removing existing mapping for $DriveLetter..." -ForegroundColor Yellow;
            net use $DriveLetter /delete /yes;
        }
        
        # Create credential object
        $securePassword = ConvertTo-SecureString $Password -AsPlainText -Force;
        $credential = New-Object System.Management.Automation.PSCredential($Username, $securePassword);
        
        # Mount the share with persistence
        $result = net use $DriveLetter $UncPath /user:$Username $Password /persistent:yes;
        
        if ($LASTEXITCODE -eq 0) {
            Write-Host "Successfully mounted $Description to $DriveLetter" -ForegroundColor Green;
        } else {
            Write-Host "Failed to mount $Description (Exit code: $LASTEXITCODE)" -ForegroundColor Red;
            Write-Host "Error output: $result" -ForegroundColor Red;
        }
        
    } catch {
        Write-Host "Error mounting $Description : $($_.Exception.Message)" -ForegroundColor Red;
    }
}

Write-Host "Starting NAS share mounting..." -ForegroundColor Green;

# Mount each configured share
foreach ($share in $nasShares) {
    Mount-NASShare -UncPath $share.UncPath -DriveLetter $share.DriveLetter -Username $share.Username -Password $share.Password -Description $share.Description;
}

Write-Host "NAS share mounting completed." -ForegroundColor Green;

